
public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=15;
		
		for (int j=1;j<=i;j++)
		{
			//System.out.println(j);
			
			if(j%2==0)
			{
				System.out.println(j+ " is even number");
			}
			else
			{
				System.out.println(j+ " is odd number");
			}
		}
		
	}

}
